from bisect import bisect_right


def longest_obstacle_course(obstacles):
    ans, active = [0] * len(obstacles), []
    for i, obstacle in enumerate(obstacles):
        idx = bisect_right(active, obstacle)
        ans[i] = idx + 1
        if idx == len(active):
            active.append(obstacle)
        else:
            active[idx] = obstacle
    return ans

