      "CREATE TABLE IF NOT EXISTS employees.employee_info ("
      "id UUID PRIMARY KEY,"
      "name text,"
      "department text,"
      "salary double);";

  CassStatement* statement = cass_statement_new(create_keyspace_query, 0);
  CassFuture* query_future = cass_session_execute(session, statement);

  if (cass_future_error_code(query_future) != CASS_OK) {
    print_error(query_future);
    return -1;
  }

  cass_statement_free(statement);
  cass_future_free(query_future);

  statement = cass_statement_new(create_table_query, 0);
  query_future = cass_session_execute(session, statement);

  if (cass_future_error_code(query_future) != CASS_OK) {
    print_error(query_future);
    return -1;
  }

  cass_statement_free(statement);
  cass_future_free(query_future);

  // Prepare CRUD statements
  const char* insert_query =
      "INSERT INTO employees.employee_info (id, name, department, salary) VALUES (?, ?, ?, ?);";
  const char* select_query = "SELECT * FROM employees.employee_info WHERE id = ?;";
  const char* update_query = "UPDATE employees.employee_info SET salary = ? WHERE id = ?;";
  const char* delete_query = "DELETE FROM employees.employee_info WHERE id = ?;";

  CassFuture* prepare_future = cass_session_prepare(session, insert_query);
  CassPrepared* prepared_insert = nullptr;

  if (cass_future_error_code(prepare_future) != CASS_OK) {
    print_error(prepare_future);
    return -1;
  }

  prepared_insert = cass_future_get_prepared(prepare_future);
  cass_future_free(prepare_future);

  // Perform basic CRUD operations using prepared statements

  // Insert an employee record
  CassUuidGen* uuid_gen = cass_uuid_gen_new();
  CassUuid employee_id;
  cass_uuid_gen_time(uuid_gen, &employee_id);

