using json = nlohmann::json;
using namespace sqlite;

struct Product {
    int id;
    std::string name;
    double price;
    std::string category;
};

void createTable(database& db) {
    db << "CREATE TABLE IF NOT EXISTS products (id INTEGER PRIMARY KEY AUTOINCREMENT, name TEXT, price REAL, category TEXT);";
}

void insertProduct(database& db, const Product& product) {
    db << "INSERT INTO products (name, price, category) VALUES (?, ?, ?);" << product.name << product.price << product.category;
}

std::vector<Product> readProductsFromJSON(const std::string& filename) {
    std::ifstream file(filename);
    if (!file.is_open()) {
        throw std::runtime_error("Could not open file");
    }

    json j;
    file >> j;

    std::vector<Product> products;
    for (const auto& item : j) {
        products.push_back({0, item["name"].get<std::string>(), item["price"].get<double>(), item["category"].get<std::string>()});
    }

    return products;
}

std::vector<Product> getFilteredAndSortedProducts(database& db, const std::string& category) {
    std::vector<Product> products;
    db << "SELECT id, name, price, category FROM products WHERE category = ? ORDER BY price ASC;" << category
       >> [&](int id, std::string name, double price, std::string cat) {
              products.push_back({id, name, price, cat});
          };

    return products;
}

void printProducts(const std::vector<Product>& products) {
    for (const auto& product : products) {
        std::cout << "ID: " << product.id << ", Name: " << product.name << ", Price: " << product.price << ", Category: " << product.category << std::endl;
    }
}

int main() {
    try {
        // Initialize the database and create a table
        database db("products.db");
        createTable(db);

        // Read products from JSON file
        const std::string filename = "products.json";
        std::vector<Product> products = readProductsFromJSON(filename);

        // Insert products into the SQLite database
        for (const auto& product : products) {
            insertProduct(db, product);
        }

        // Filter and sort products by category and price
        std::string category = "Electronics";
        std::vector<Product> filteredAndSortedProducts = getFilteredAndSortedProducts(db, category);

        // Print filtered and sorted products
        std::cout << "Filtered and sorted products in category '" << category << "':" << std::endl;
        printProducts(filteredAndSortedProducts);
    } catch (const std::exception& e) {
        std::cerr << "Error: " << e.what() << std::endl;
        return 1;
    }

    return 0;
}

def average_waiting_time(customers):
    wait_time = 0
    curr_time = 0

    for arrival, time in customers:
        curr_time = max(curr_time, arrival) + time
        wait_time += curr_time - arrival

    return wait_time / len(customers)

