package main

import (
	"fmt"
	"net/http"
	"sync"
	"time"

	"github.com/gorilla/mux"
	"golang.org/x/time/rate"
)

type rateLimiter struct {
	sync.RWMutex
	clients map[string]*rate.Limiter
}

func newRateLimiter() *rateLimiter {
	return &rateLimiter{
		clients: make(map[string]*rate.Limiter),
	}
}

func (r *rateLimiter) getLimiter(ip string) *rate.Limiter {
	r.Lock()
	defer r.Unlock()

	limiter, exists := r.clients[ip]
	if !exists {
		limiter = rate.NewLimiter(rate.Every(time.Minute/10), 1)
		r.clients[ip] = limiter
	}
	return limiter
}

func (r *rateLimiter) cleanup() {
	for range time.Tick(5 * time.Minute) {
		r.Lock()
		for ip, limiter := range r.clients {
			if limiter.AllowN(time.Now(), 10) {
				delete(r.clients, ip)
			}
		}
		r.Unlock()
	}
}

func currentTimeHandler(w http.ResponseWriter, req *http.Request) {
	w.Header().Set("Content-Type", "text/plain")
	fmt.Fprintf(w, "Current server time is: %s\n", time.Now().Format(time.RFC1123))
}

func main() {
	router := mux.NewRouter()
	rl := newRateLimiter()
	go rl.cleanup()

	router.HandleFunc("/", func(w http.ResponseWriter, req *http.Request) {
		ip := req.RemoteAddr
		limiter := rl.getLimiter(ip)

		if !limiter.Allow() {
			http.Error(w, "Too many requests. Please try again later.", http.StatusTooManyRequests)
			return
		}

		currentTimeHandler(w, req)
	})

	http.ListenAndServe(":8080", router)
}

