function topThreeScorers(arr) {
  // Sort the array by score in descending order
  arr.sort((a, b) => b.score - a.score);

  // Initialize an empty array to store the top 3 names
  let topNames = [];

  // Iterate through the sorted array and add the first 3 unique names to the topNames array
  for (let i = 0; i < arr.length && topNames.length < 3; i++) {
    if (!topNames.includes(arr[i].name)) {
      topNames.push(arr[i].name);
    }
  }

